//             {name: 'Beef Patty', color: '#3F250B'},
//             {name: 'Soy Patty', color: '#3F250B'},
//             {name: 'Black Bean Patty', color: '#3F250B'},
//             {name: 'Chicken Patty', color: 'burlywood'},
//             {name: 'Lettuce', color: 'lawngreen'},
